--EXAMPLES OF JOINS--TableA = 4,7,8, 10,11--TableB = 4,8,12--TableC = 10,13,12, 15--Right join: SELECT * FROM TABLEA RIGHT JOIN TABLEBON A.ID = B.ID--RESULT 4,8,12 --Left join:SELECT * FROM TABLEA LEFT JOIN TABLEBON A.ID = B.ID--RESULT = 4,7,8,10,11--Inner join:SELECT * FROM TABLEA INNER JOIN TABLEBON A.ID = B.ID--RESULT = 4,8--Inner join:SELECT * FROM TABLEA INNER JOIN TABLEBON A.ID = B.IDINNER JOIN TABLECON C.ID = A.ID--RESULT = 4,8,10