CREATE TABLE EMADEPHARMACY(ID INT NOT NULL, DRUGNAME VARCHAR(50) NULL, PRICE INT NOT NULL, LOCATION VARCHAR(50) NOT NULL)INSERT INTO EMADEPHARMACYVALUES(1,'TYLENOL',50,'ELKGROVE'),(2,'IBUPROFEN',70,'SAC'),(3,'ADVIL',30,'ROSEVILLE')--Check Table select*From EMADEPHARMACY--Case Statement if the price is $30 make it cheap and if the price is 70 make it expensive and Make 50 affordableselect ID, DrugName,Price,Location,Case When price = 30 then 'cheap'When price = 70 then 'expensive'When price = 50 then 'affordable'end as PricestatusFrom EMADEPHARMACY