CREATE TABLE EMADECONSULTINGEMPLOYEE(EMPLOYEE_ID INT NOT NULL,LAST_NAME VARCHAR(20) NULL,FIRST_NAME VARCHAR(20) NULL,MIDDLE_NAME VARCHAR(20) NULL,JOB_ID INT NOT NULL,MANAGER_ID INT NOT NULL, HIRE_DATE DATE NULL,SALARY MONEY NULL,COMM INT NULL,DEPARTMENT_ID INT NOT NULL)INSERT INTO EMADECONSULTINGEMPLOYEEVALUES(7369, 'SMITH', 'JOHN', 'Q', 667, 7902, '17-DEC-84', 800, NULL, 20),(7499, 'ALLEN', 'KEVIN', 'J', 670, 7698, '20-FEB-85', 1600, 300, 30),(7505, 'DOYLE', 'JEAN', 'K', 671, 7839, '04-APR-85', 2850, NULL, 30),(7506, 'DENNIS', 'LYNN', 'S', 671, 7839, '15-MAY-85', 2750, NULL, 30),(7507, 'BAKER', 'LESLIE', 'D', 671, 7839, '10-JUN-85', 2200, NULL, 40),(7521, 'WARK', 'CYNTHIA', 'D', 670, 7698, '22-FEB-85', 1250, 500, 30)
--CHECK NEWLY CREATED TABLESSELECT *FROM EMADECONSULTINGLOCATIONSELECT *FROM EMADECONSULTINGDEPARTMENTSELECT *FROM EMADECONSULTINGJOBSELECT *FROM EMADECONSULTINGEMPLOYEE--QUESTION 1- DISPLAY THE EMPLOYEE DETAILS WITH SALARY GRADES . --Give salary not more than 1000 to Intern and give salary higher than 2000 as Junior Staff the other salaries to Senior StaffSELECT 	A.FIRST_NAME,	A.MIDDLE_NAME,	A.LAST_NAME,	A.HIRE_DATE,	A.SALARY,	B.DESIGNATION,CASE	WHEN SALARY < 1000 THEN 'INTERN'	WHEN SALARY > 2000 THEN 'JUNIOR STAFF'	ELSE 'SENIOR STAFF'	END AS 'SALARY GRADE'FROM EMADECONSULTINGEMPLOYEE A,	 EMADECONSULTINGJOB BWHERE A.JOB_ID=B.JOB_ID--using inner joinSELECT 	A.FIRST_NAME,	A.MIDDLE_NAME,	A.LAST_NAME,	A.HIRE_DATE,	A.SALARY,	B.DESIGNATION,CASE	WHEN SALARY < 1000 THEN 'INTERN'	WHEN SALARY > 2000 THEN 'JUNIOR STAFF'	ELSE 'SENIOR STAFF'	END AS 'SALARY GRADE'FROM EMADECONSULTINGEMPLOYEE AJoin EMADECONSULTINGJOB BOn A.JOB_ID=B.JOB_IDHOMEWORK ASSIGNMENT--QUESTION 2 - LIST OUT ALL THE JOBS IN SALES AND ACCOUNTING DEPARTMENTS
--QUESTION 3 - DISPLAY THE EMPLOYEES LIST WITH HIGHEST SALARY


Query to Question #2

--using inner joinSELECT 	A.FIRST_NAME,	A.LAST_NAME,	A.HIRE_DATE,	A.SALARY,	B.DESIGNATION,	B.CITYCASE	WHEN SALARY < 1000 THEN 'INTERN'	WHEN SALARY > 2000 THEN 'JUNIOR STAFF'	ELSE 'SENIOR STAFF'	END AS 'SALARY GRADE'FROM EMADECONSULTINGEMPLOYEE AJoin EMADECONSULTINGJOB BOn A.JOB_ID=B.JOB_ID